-- @BLOCK Dropping a database if exists
DROP DATABASE IF EXISTS `Messaging-App`;

-- @BLOCK Creating a database to store all tables of the application
CREATE DATABASE `Messaging-App`;

-- @BLOCK Using the database
USE `Messaging-App`;

-- @BLOCK Creating the table to store all the users 
CREATE TABLE `User`(
    `id` INT UNSIGNED NOT NULL AUTO_INCREMENT,
    `firstname` VARCHAR(20) NOT NULL,
    `lastname` VARCHAR(20) NOT NULL,
    `profilepic` TEXT NOT NULL,
    `bio` TEXT NOT NULL,
    `emailid` TEXT NOT NULL,
    `password` VARCHAR(255) NOT NULL,
    `last_login` DATETIME NOT NULL,
    `theme` VARCHAR(6) NOT NULL,
    `wallpaper` TEXT NOT NULL,
    PRIMARY KEY (id)
);

-- @BLOCK Inserting User the table to store all the users 

-- @BLOCK Creating the table to store all the messages
CREATE TABLE `Message`(
    `id` INT UNSIGNED NOT NULL AUTO_INCREMENT,
    `user_id` INT UNSIGNED NOT NULL,
    `created_at` DATETIME NOT NULL,
    `media_url` TEXT NULL,
    `msg_text` TEXT NOT NULL,
    `file_url` TEXT NULL,
    PRIMARY KEY (id),
    FOREIGN KEY (user_id) REFERENCES User(id)
);

-- @BLOCK Inserting messages


-- @BLOCK Creating the table to store all the groups
CREATE TABLE `Group`(
    `id` INT UNSIGNED NOT NULL AUTO_INCREMENT,
    `name` VARCHAR(32) NOT NULL,
    `profile_pic` TEXT NOT NULL,
    `bio` TEXT NOT NULL,
    `owner_id` INT UNSIGNED NOT NULL,
    PRIMARY KEY (id),
    FOREIGN KEY (owner_id) REFERENCES User(id)
);

-- @BLOCK Inserting Groups

-- @BLOCK Creating the table to store all the Chatboxes
CREATE TABLE `Chatbox`(
    `id` INT UNSIGNED NOT NULL AUTO_INCREMENT,
    `last_message_id` INT UNSIGNED NULL,
    `group_id` INT UNSIGNED NULL,
    PRIMARY KEY (id),
    FOREIGN KEY (last_message_id) REFERENCES Message(id),
    FOREIGN KEY (group_id) REFERENCES `Group`(id)
);

-- @BLOCK Inserting Chatboxes


-- @BLOCK Creating the table to map Users, Chatbox, Message
CREATE TABLE `Chatbox_msg`(
    `user_id` INT UNSIGNED NOT NULL,
    `chatbox_id` INT UNSIGNED NOT NULL,
    `msg_id` INT UNSIGNED NOT NULL,
    FOREIGN KEY (user_id) REFERENCES User(id),
    FOREIGN KEY (chatbox_id) REFERENCES Chatbox(id),
    FOREIGN KEY (msg_id) REFERENCES Message(id)
);

-- @BLOCK Mapping the table.

-- @BLOCK Creating the table to store all the Read Reciepts
CREATE TABLE `Read_Reciepts`(
    `msg_id` INT UNSIGNED NOT NULL,
    `user_id` INT UNSIGNED NOT NULL,
    `delivered_time` DATETIME NULL,
    `read_time` DATETIME NULL,
    FOREIGN KEY (msg_id) REFERENCES Message(id),
    FOREIGN KEY (user_id) REFERENCES User(id)
);

-- @BLOCK Inserting Read Reciepts.


